import React, { Component } from "react";
import { Breadcrumb } from "@gull";
import { Dropdown } from "react-bootstrap";
import { Table } from "react-bootstrap";
import ReactPaginate from "react-paginate";
import { Link } from "react-router-dom";
import Chart from "react-apexcharts";
import Echart1 from "../ui-kits/card-metrics/Echart1";
import Echart2 from "../ui-kits/card-metrics/Echart2";
import Echart3 from "../ui-kits/card-metrics/Echart3";
import Echart4 from "../ui-kits/card-metrics/Echart4";
import Echart5 from "../ui-kits/card-metrics/Echart5";
import Echart6 from "../ui-kits/card-metrics/Echart6";
import Echart7 from "../ui-kits/card-metrics/Echart7";
import Echart8 from "../ui-kits/card-metrics/Echart8";
import Echart9 from "../ui-kits/card-metrics/Echart9";
import Echart10 from "../ui-kits/card-metrics/Echart10";
import { ProgressBar } from "react-bootstrap";

import {
  bAOptions1,
  bAOptions2,
  bAOptions3,
  bAOptions4,
  bAOptions5,
  bAOptions6,
  bAOptions7,
  bAOptions8,
  bAOptions9,
  bAOptions10,
  bAOptions11,
  bAOptions12,
  bAOptions13,
  bAOptions14,
  bAOptions15,
  bAOptions16,
  bAOptions17,
  bAOptions18,
  bAOptions19,
  bAOptions20,
  bAOptions21,
  bAOptions22,
  bAOptions23
} from "./DashboardOptions";


class Dashboard12m extends Component {
  state = {
    cardList1: [
      {
        icon: "i-Car-Items",
        title: "New Leads",
        amount: 205
      },
      {
        icon: "i-Data-Download",
        title: "Download",
        amount: 4021
      },
      {
        icon: "i-Cube-Molecule-2",
        title: "Feedback",
        amount: 80
      },
      {
        icon: "i-File-Horizontal-Text",
        title: "Option",
        amount: 1200
      }
    ],

    chartList1: [
      {
        title: "INCOME",
        amount: 1000,
        chart: bAOptions1
      },
      {
        title: "APPROVE",
        amount: 500,
        chart: bAOptions2
      },
      {
        title: "TRANSACTION",
        amount: 44909,
        chart: bAOptions3
      },
      {
        title: "SALES",
        amount: 500,
        chart: bAOptions4
      }
    ],

    chartList2: [
      {
        icon: "i-Like-2",
        title: "Approve",
        amount: 55000,
        chart: bAOptions5
      },
      {
        icon: "i-File-Horizontal-Text",
        title: "Comments",
        amount: 500,
        chart: bAOptions6
      },
      {
        icon: "i-Cube-Molecule-2",
        title: "Contribution",
        amount: 46000,
        chart: bAOptions7
      },
      {
        icon: "i-Eye",
        title: "Watch",
        amount: 70000,
        chart: bAOptions8
      }
    ],

    chartList3: [
      {
        title: "People",
        amount: 2750,
        chart: bAOptions9
      },
      {
        title: "Use",
        amount: "75%",
        chart: bAOptions10
      },
      {
        title: "Views",
        amount: 2750,
        chart: bAOptions11
      },
      {
        title: "Growth",
        amount: 6760,
        chart: bAOptions12
      }
    ],

    chartList4: [
      {
        amount: 698212,
        chart: bAOptions13
      },
      {
        amount: 369212,
        chart: bAOptions14
      },
      {
        amount: 369212,
        chart: bAOptions15
      },
      {
        amount: 369212,
        chart: bAOptions16
      }
    ],
    cardList2: [
      {
        icon: "i-Building",
        color: "text-primary",
        title: "Assets",
        amount: "46,025"
      },
      {
        icon: "i-Bar-Chart",
        color: "text-danger",
        title: "Progression",
        amount: "80%"
      },
      {
        icon: "i-Full-Cart",
        color: "text-success",
        title: "Total Profit",
        amount: "\u09F3 2000"
      },
      {
        icon: "i-Bookmark",
        color: "text-warning",
        title: "Lease",
        amount: "5,417"
      }
    ],
    chartList5: [
      {
        title: "People Choice Rate",
        chart: bAOptions17
      },
      {
        title: "Item Pending",
        chart: bAOptions18
      },
      {
        title: "Total Profit",
        chart: bAOptions19
      },
      {
        title: "Total Revenue",
        chart: bAOptions20
      }
    ],
    chartList6: [
      {
        title: "Orders Received",
        chart: bAOptions21,
        amount: 697
      },
      {
        title: "Total Marketing",
        chart: bAOptions22,
        amount: "5,767"
      },
      {
        title: "Total Earning",
        chart: bAOptions23,
        amount: "$5,767"
      }
    ],
    cardList3: [
      {
        title: "Server status region 1",
        subtitle: "Last down 4 days ago",
        icon: "i-Arrow-Up-in-Circle",
        color: "success",
        message: "Up"
      },
      {
        title: "Server status region 2",
        subtitle: "Last down 8 days ago",
        icon: "i-Arrow-Up-in-Circle",
        color: "success",
        message: "Up"
      },
      {
        title: "Server status region 3",
        subtitle: "Last down 22 days ago",
        icon: "i-Arrow-Down-in-Circle",
        color: "danger",
        message: "Down"
      },
      {
        title: "Server status region 4",
        subtitle: "Last down 2 days ago",
        icon: "i-Arrow-Down-in-Circle",
        color: "danger",
        message: "Down"
      }
    ],
    activePage: 1,

  };

  setOpenItem = (name) => {
    if (name === this.state.openItem) {
      this.setState({ openItem: "" });
    } else this.setState({ openItem: name });
  };

  handlePageClick = (data) => {
    let currentPage = data.selected;
    this.setState({ currentPage });
  };

  getUserEventClass = (eventCode) => {
    switch (eventCode) {
      case "A4000":
        return "bg-success";
      case "A4001":
        return "bg-success";
      case "MAINT":
        return "bg-warning";
      case "not delivered":
        return "bg-warning";
      case "P4000":
        return "bg-primary";
      default:
        break;
    }
  };

  getStatusTextColor = (eventCode) => {
    switch (eventCode) {
      case "In Stock":
        return "text-success";
      case "Low Stock":
        return "text-warning";
      case "Out of Stock":
        return "text-danger";
      default:
        return "text-primary";
    }
  };

  render() {
    let {
      cardList1,
      cardList2,
      cardList3,
      chartList1,
      chartList2,
      chartList3,
      chartList4,
      chartList5,
      chartList6
    } = this.state;

    return (
      <div>
        <Breadcrumb className="operatorNavbar"
          routeSegments={[
            { name: "Home", path: "/" },
            { name: "Pages", path: "/pages" },
            { name: "Log", path : "/pages/operators-log" },
            { name: "Records", path: "/pages/records" },
            { name: "12M Radio Telescope Dashboard" },
          ]}
        ></Breadcrumb>

      </div>
    );
  }
}

export default Dashboard12m;
